function songsList(id) {
	allSongs = JSON.parse(localStorage.getItem('allSongs'));
	noSuchSong = JSON.parse(localStorage.getItem('noSuchSong'));
	to_play = []
	for (x in id){
		i = id[x]
		if (allSongs[i-1] === undefined){
			to_play.push(noSuchSong)
		}else{
			to_play.push(allSongs[i-1])
		}
	}
	return to_play
}
function getSongFromId(id) {
	if (id <= allSongs.length && id > 0) {
		return allSongs[id-1]
	}else{
		return noSuchSong
	}
}
function getQueryInt(variable){ 
  var query = window.location.search.substring(1); 
  var vars = query.split("&"); 
  for (var i=0;i<vars.length;i++)
  { 
    var pair = vars[i].split("="); 
    if (pair[0] == variable)
    { 
      final = []
      result = pair[1].split(',');
      for (var x in result) {
      	var i = result[x]
      	final.push(parseInt(i))
      }
      return final	
    } 
  }
  return [noSuchSong]
}
function getQueryVariable(variable){ 
  var query = window.location.search.substring(1); 
  var vars = query.split("&"); 
  for (var i=0;i<vars.length;i++)
  { 
    var pair = vars[i].split("="); 
    if (pair[0] == variable)
    { 
      result = pair[1];
      return result
    } 
  }
  return false
}
function addToPlaylist(name, id, remove=false) {
	playlists = JSON.parse(localStorage.getItem('playlists'));
	for (var i in playlists) {
		if (playlists[i].name == name){
			if (!remove) {
				for (var x in playlists[i].songs) {
					if (playlists[i].songs[x] == id) {
						return false
					}
				}
				playlists[i].songs.push(id)
			}else{
				for (var x in playlists[i].songs) {
					if (playlists[i].songs[x] == id){
						playlists[i].songs.splice(x, 1)
					}
				}
			}
		}
	}
	localStorage.setItem('playlists', JSON.stringify(playlists));
}
function addToFavourites(id, remove=false) {
	var favorite = JSON.parse(localStorage.getItem('favourites'));
	for (var i in favorite) {
		if (favorite[i] == id){
			if (remove) {
				favorite.splice(i, 1)
				localStorage.setItem('favourites', JSON.stringify(favorite));
				return true
			}else{
				return false
			}
		}
	}
	favorite.push(id)
	localStorage.setItem('favourites', JSON.stringify(favorite));
	return true
}
function createPlaylist(name){
	var playlist = JSON.parse(localStorage.getItem('playlists'));
	for (var i in playlist) {
		if (playlist[i].name==name) {
			return false
		}
	}
	playlist.push({"name":name,songs:[], img: userPlaylistImages[Math.floor(Math.random() * userPlaylistImages.length)]})
	localStorage.setItem('playlists', JSON.stringify(playlist));

	return true
}
  function changeIconColor(obj) {
    obj.classList.toggle("color-red")
    for (var i in obj.classList) {
    	if (obj.classList[i]=="color-red") {
    		if (!inFavourites(obj.classList[0])) {
    			addToFavourites(obj.classList[0])
    		}
    	}
    	else if (obj.classList.length <= 1){
    		if (inFavourites(obj.classList[0])) {
    			addToFavourites(obj.classList[0], remove=true);
    		}
    	}
    }
  }
function inFavourites(id) {
    var userFavourites = JSON.parse(localStorage.getItem('favourites'));
    for (var i in userFavourites) {
        if (userFavourites[i] == id) {
            return true
        }
    }
    return false
}
function inPlaylist(name) {
    var userPlaylist = JSON.parse(localStorage.getItem('playlists'));
    for (var i in userPlaylist) {
        if (userPlaylist[i].name == name) {
            return true
        }
    }
    return false
}
function closeAlert(name){
	document.getElementsByClassName(`alert ${name}`)[0].style.display = "none"
}
function getPlaylistDetailsId(id){
	var AllPlaylist = JSON.parse(localStorage.getItem('playlists'))
	if (id > 0 && id <= AllPlaylist.length) {
		return AllPlaylist[id-1]
	}else{
		return false
	}
}
function getPlaylistDetailsTypeId(id, type="r"){
	if (type == "u") {
		var AllPlaylist = JSON.parse(localStorage.getItem('playlists'))
		if (id > 0 && id <= AllPlaylist.length) {
			return AllPlaylist[id-1]
		}else{
			return false
		}
	}else{
		readyMade = JSON.parse(localStorage.getItem('readyMade'))
		if (id > 0 && id <= readyMade.length) {
			return readyMade[id-1]
		}else{
			return false
		}
	}
}
function getSongIdFromSong(songDetails){
	var allSongsApp = JSON.parse(localStorage.getItem('allSongs'))
	for (var i in allSongsApp) {
		if (allSongsApp[i].name==songDetails.name) {
			return parseInt(i)+1
		}
	}
	return false
}
